import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar';
import Login from './components/Login';
import FacultyDashboard from './components/FacultyDashboard';
import StudentDashboard from './components/StudentDashboard';
import ForgotPassword from './components/ForgotPassword'; // Import the new component
import './styles.css';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      // Verify token and set user (simplified, add actual JWT verification if needed)
      setUser(JSON.parse(localStorage.getItem('user') || null));
    }
  }, []);

  return (
    <Router>
      <div className="app">
        <Navbar user={user} setUser={setUser} />
        <div className="content">
          <Routes>
            <Route path="/" element={user ? <Navigate to={user.role === 'student' ? '/student' : '/faculty'} /> : <Login setUser={setUser} />} />
            <Route path="/faculty" element={user && user.role === 'faculty' ? <FacultyDashboard user={user} /> : <Navigate to="/" />} />
            <Route path="/student" element={user && user.role === 'student' ? <StudentDashboard user={user} /> : <Navigate to="/" />} />
            <Route path="/forgot-password" element={<ForgotPassword />} /> {/* Add forgot password route */}
            <Route path="/register" element={<div>Registration Page (to be implemented)</div>} /> {/* Placeholder for registration */}
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
