import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Modal, Card, Alert } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function StudentDashboard({ user }) {
  const [attendance, setAttendance] = useState([]);
  const [profile, setProfile] = useState(user || {});
  const [editMode, setEditMode] = useState(false);
  const [schedule, setSchedule] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [message, setMessage] = useState('');
  const [notes, setNotes] = useState(localStorage.getItem(`notes_${user._id}`) || '');

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/attendance?studentId=${profile.studentId}`, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setAttendance(res.data);
      } catch (err) {
        setMessage('Failed to fetch attendance: ' + (err.response?.data?.msg || err.message));
        setShowModal(true);
      }
    };
    const fetchSchedule = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/schedule', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setSchedule(res.data);
      } catch (err) {
        setMessage('Failed to fetch schedule: ' + (err.response?.data?.msg || err.message));
        setShowModal(true);
      }
    };
    if (profile.studentId) {
      fetchAttendance();
      fetchSchedule();
    }
  }, [profile.studentId]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(
        `http://localhost:5000/api/users/${profile._id}`,
        {
          name: profile.name,
          email: profile.email,
          studentId: profile.studentId,
          branch: profile.branch,
          section: profile.section,
        },
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      setProfile(res.data);
      setMessage('Profile updated successfully!');
      setEditMode(false);
      setShowModal(true);
    } catch (err) {
      setMessage('Failed to update profile: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
    }
  };

  const calculateAttendancePercentage = () => {
    if (attendance.length === 0) return 0;
    const presentCount = attendance.filter((a) => a.status === 'present').length;
    return ((presentCount / attendance.length) * 100).toFixed(2);
  };

  const handleNotesChange = (e) => {
    setNotes(e.target.value);
    localStorage.setItem(`notes_${user._id}`, e.target.value);
  };

  const attendanceData = {
    labels: attendance.map((a) => new Date(a.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Attendance Status',
        data: attendance.map((a) => (a.status === 'present' ? 1 : 0)),
        borderColor: '#d32f2f',
        backgroundColor: 'rgba(211, 47, 47, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="dashboard-content">
      <h2>Welcome, {profile.name}</h2>
      <div className="row">
        <div className="col-md-4">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Profile</Card.Title>
              {editMode ? (
                <Form onSubmit={handleUpdateProfile}>
                  <Form.Group className="form-group">
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                      type="text"
                      value={profile.name || ''}
                      onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                      required
                    />
                  </Form.Group>
                  <Form.Group className="form-group">
                    <Form.Label>Email</Form.Label>
                    <Form.Control
                      type="email"
                      value={profile.email || ''}
                      onChange={(e) => setProfile({ ...profile, email: e.target.value })}
                      required
                    />
                  </Form.Group>
                  <Form.Group className="form-group">
                    <Form.Label>Student ID</Form.Label>
                    <Form.Control
                      type="text"
                      value={profile.studentId || ''}
                      onChange={(e) => setProfile({ ...profile, studentId: e.target.value })}
                      required
                    />
                  </Form.Group>
                  <Form.Group className="form-group">
                    <Form.Label>Branch</Form.Label>
                    <Form.Control
                      type="text"
                      value={profile.branch || ''}
                      onChange={(e) => setProfile({ ...profile, branch: e.target.value })}
                      required
                    />
                  </Form.Group>
                  <Form.Group className="form-group">
                    <Form.Label>Section</Form.Label>
                    <Form.Control
                      type="text"
                      value={profile.section || ''}
                      onChange={(e) => setProfile({ ...profile, section: e.target.value })}
                      required
                    />
                  </Form.Group>
                  <Button className="btn-danger" type="submit">
                    Save
                  </Button>
                  <Button variant="secondary" onClick={() => setEditMode(false)} className="ms-2">
                    Cancel
                  </Button>
                </Form>
              ) : (
                <>
                  <p><strong>Email:</strong> {profile.email || 'N/A'}</p>
                  <p><strong>Student ID:</strong> {profile.studentId || 'N/A'}</p>
                  <p><strong>Branch:</strong> {profile.branch || 'N/A'}</p>
                  <p><strong>Section:</strong> {profile.section || 'N/A'}</p>
                  <p><strong>Attendance:</strong> {calculateAttendancePercentage()}%</p>
                  <Button className="btn-danger" onClick={() => setEditMode(true)}>
                    Edit Profile
                  </Button>
                </>
              )}
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Notes</Card.Title>
              <Form.Control
                as="textarea"
                rows={5}
                value={notes}
                onChange={handleNotesChange}
                placeholder="Add your notes here..."
              />
            </Card.Body>
          </Card>
        </div>
        <div className="col-md-8">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Attendance Records</Card.Title>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Time</th>
                    <th>Subject</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.map((record) => (
                    <tr key={record._id}>
                      <td>{new Date(record.date).toLocaleDateString()}</td>
                      <td>{new Date(record.date).toLocaleTimeString()}</td>
                      <td>{record.subject || 'N/A'}</td>
                      <td style={{ color: record.status === 'present' ? 'green' : 'red' }}>
                        {record.status}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Attendance Trend</Card.Title>
              <Line data={attendanceData} />
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Class Schedule</Card.Title>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Day</th>
                    <th>Time</th>
                    <th>Subject</th>
                  </tr>
                </thead>
                <tbody>
                  {schedule.map((classItem) => (
                    <tr key={classItem._id}>
                      <td>{classItem.day}</td>
                      <td>{classItem.time}</td>
                      <td>{classItem.subject}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </div>
      </div>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{message.includes('successfully') ? 'Success' : 'Error'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{message}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default StudentDashboard;
