import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Modal } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [showErrorPopup, setShowErrorPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('http://localhost:5000/api/login', { userIdOrEmail, password }, {
        headers: { 'Content-Type': 'application/json' }
      });
      localStorage.setItem('token', res.data.token);
      setUser(res.data.user);
      setPopupMessage('Login successful! Redirecting...');
      setShowSuccessPopup(true);
      setTimeout(() => {
        setShowSuccessPopup(false);
        if (res.data.user.role === 'student') {
          navigate('/student');
        } else if (res.data.user.role === 'faculty') {
          navigate('/faculty');
        }
      }, 2000);
    } catch (err) {
      const errorMessage = err.response?.data?.msg || err.message || 'Login failed. Please check your credentials.';
      setPopupMessage(errorMessage);
      setShowErrorPopup(true);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>CUAttendance</h2>
        <p>Welcome to University Attendance Management â€“ Your gateway to seamless attendance tracking!</p>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Label>User ID or Email</Form.Label>
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="24MC10250 or email@example.com"
              required
              autoComplete="username"
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
              autoComplete="current-password"
            />
          </Form.Group>
          <Button className="btn-login" type="submit">
            LOGIN
          </Button>
          <a href="/forgot-password" className="forgot-password">
            Forgot Password?
          </a>
          <Button
            variant="link"
            onClick={() => navigate('/register')}
            style={{ color: '#1976d2', textDecoration: 'none', marginTop: '0.5rem', display: 'block' }}
          >
            Register
          </Button>
        </Form>

        <Modal show={showSuccessPopup} onHide={() => setShowSuccessPopup(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Success</Modal.Title>
          </Modal.Header>
          <Modal.Body>{popupMessage}</Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => setShowSuccessPopup(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>

        <Modal show={showErrorPopup} onHide={() => setShowErrorPopup(false)}>
          <Modal.Header closeButton>
            <Modal.Title>Error</Modal.Title>
          </Modal.Header>
          <Modal.Body>{popupMessage}</Modal.Body>
          <Modal.Footer>
            <Button variant="primary" onClick={() => setShowErrorPopup(false)}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      </div>
    </div>
  );
}

export default Login;
