import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Modal } from 'react-bootstrap';

function FacultyDashboard({ user }) {
  const [sections, setSections] = useState([]);
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [summary, setSummary] = useState({ present: 0, absent: 0 });
  const [notification, setNotification] = useState('');
  const [schedule, setSchedule] = useState([]);
  const [showSubmitPopup, setShowSubmitPopup] = useState(false);
  const [newSchedule, setNewSchedule] = useState({ day: '', time: '', subject: '' });
  const [editScheduleId, setEditScheduleId] = useState(null);
  const [attendanceHistory, setAttendanceHistory] = useState([]);

  useEffect(() => {
    const fetchSections = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/sections', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setSections(res.data);
      } catch (err) {
        setNotification('Failed to fetch sections: ' + (err.response?.data?.msg || err.message));
        setShowSubmitPopup(true);
      }
    };
    const fetchSchedule = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/schedule', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setSchedule(res.data);
      } catch (err) {
        setNotification('Failed to fetch schedule: ' + (err.response?.data?.msg || err.message));
        setShowSubmitPopup(true);
      }
    };
    const fetchAttendanceHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/attendance', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setAttendanceHistory(res.data);
      } catch (err) {
        setNotification('Failed to fetch attendance history: ' + (err.response?.data?.msg || err.message));
        setShowSubmitPopup(true);
      }
    };
    fetchSections();
    fetchSchedule();
    fetchAttendanceHistory();
  }, []);

  const handleFilter = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/users?section=${selectedSection}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setStudents(res.data);
      setSummary({ present: 0, absent: 0 });
      setAttendance({});
    } catch (err) {
      setNotification('Failed to filter students: ' + (err.response?.data?.msg || err.message));
      setShowSubmitPopup(true);
    }
  };

  const handleAttendanceChange = (studentId, status) => {
    setAttendance({ ...attendance, [studentId]: status });
    setSummary(prev => ({
      ...prev,
      [status]: prev[status] + 1 - (attendance[studentId] === status ? 1 : 0)
    }));
  };

  const submitAttendance = async () => {
    try {
      for (const [studentId, status] of Object.entries(attendance)) {
        await axios.post('http://localhost:5000/api/attendance', {
          studentId,
          section: selectedSection,
          subject: selectedSubject || user.subject,
          status
        }, {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
      }
      setNotification('Attendance submitted successfully!');
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
        setAttendance({});
        setSummary({ present: 0, absent: 0 });
      }, 3000);
    } catch (err) {
      setNotification('Failed to submit attendance: ' + (err.response?.data?.msg || err.message));
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    }
  };

  const exportAttendance = () => {
    const csv = [
      ['Student ID', 'Name', 'Status', 'Date', 'Time'],
      ...students.map(student => [
        student.studentId,
        student.name,
        attendance[student.studentId] || 'N/A',
        new Date().toLocaleDateString(),
        new Date().toLocaleTimeString()
      ])
    ].map(row => row.join(',')).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `attendance_${selectedSection}_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    setNotification('Attendance exported successfully!');
    setShowSubmitPopup(true);
    setTimeout(() => {
      setNotification('');
      setShowSubmitPopup(false);
    }, 3000);
  };

  const exportSchedule = () => {
    const csv = [
      ['Day', 'Time', 'Subject'],
      ...schedule.map(item => [item.day, item.time, item.subject])
    ].map(row => row.join(',')).join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `schedule_${user.name}_${new Date().toISOString().split('T')[0]}.csv`;
    a.click();
    setNotification('Schedule exported successfully!');
    setShowSubmitPopup(true);
    setTimeout(() => {
      setNotification('');
      setShowSubmitPopup(false);
    }, 3000);
  };

  const handleCreateSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/schedule', newSchedule, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setNewSchedule({ day: '', time: '', subject: '' });
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSchedule(res.data);
      setNotification('Schedule created successfully!');
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    } catch (err) {
      setNotification('Failed to create schedule: ' + (err.response?.data?.msg || err.message));
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    }
  };

  const handleEditSchedule = (id) => {
    const scheduleToEdit = schedule.find(s => s._id === id);
    setNewSchedule({ day: scheduleToEdit.day, time: scheduleToEdit.time, subject: scheduleToEdit.subject });
    setEditScheduleId(id);
  };

  const handleUpdateSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.put(`http://localhost:5000/api/schedule/${editScheduleId}`, newSchedule, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setNewSchedule({ day: '', time: '', subject: '' });
      setEditScheduleId(null);
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSchedule(res.data);
      setNotification('Schedule updated successfully!');
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    } catch (err) {
      setNotification('Failed to update schedule: ' + (err.response?.data?.msg || err.message));
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    }
  };

  const handleDeleteSchedule = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/schedule/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setSchedule(res.data);
      setNotification('Schedule deleted successfully!');
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    } catch (err) {
      setNotification('Failed to delete schedule: ' + (err.response?.data?.msg || err.message));
      setShowSubmitPopup(true);
      setTimeout(() => {
        setNotification('');
        setShowSubmitPopup(false);
      }, 3000);
    }
  };

  return (
    <div className="dashboard-content">
      <h2>Welcome, {user.name}</h2>
      <p>Subject: {user.subject}</p>
      <h3>Class Schedule</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Day</th>
            <th>Time</th>
            <th>Subject</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((classItem, index) => (
            <tr key={index}>
              <td>{classItem.day}</td>
              <td>{classItem.time}</td>
              <td>{classItem.subject}</td>
              <td>
                <Button variant="warning" onClick={() => handleEditSchedule(classItem._id)} style={{ marginRight: '0.5rem' }}>Edit</Button>
                <Button variant="danger" onClick={() => handleDeleteSchedule(classItem._id)}>Delete</Button>
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <Form onSubmit={editScheduleId ? handleUpdateSchedule : handleCreateSchedule}>
        <Form.Group className="form-group">
          <Form.Label>Day</Form.Label>
          <Form.Control
            as="select"
            value={newSchedule.day}
            onChange={(e) => setNewSchedule({ ...newSchedule, day: e.target.value })}
            required
          >
            <option value="">Select Day</option>
            {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(day => (
              <option key={day} value={day}>{day}</option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group className="form-group">
          <Form.Label>Time</Form.Label>
          <Form.Control
            type="text"
            value={newSchedule.time}
            onChange={(e) => setNewSchedule({ ...newSchedule, time: e.target.value })}
            placeholder="e.g., 10:00 AM - 11:30 AM"
            required
          />
        </Form.Group>
        <Form.Group className="form-group">
          <Form.Label>Subject</Form.Label>
          <Form.Control
            type="text"
            value={newSchedule.subject}
            onChange={(e) => setNewSchedule({ ...newSchedule, subject: e.target.value })}
            placeholder="Enter subject"
            required
          />
        </Form.Group>
        <Button className="btn-primary" type="submit">
          {editScheduleId ? 'Update Schedule' : 'Add Schedule'}
        </Button>
        <Button className="btn-primary" onClick={exportSchedule} style={{ marginLeft: '1rem' }}>
          Export Schedule
        </Button>
      </Form>

      <div style={{ marginTop: '2rem' }}>
        <Form.Group className="form-group">
          <Form.Label>Section</Form.Label>
          <Form.Control
            as="select"
            value={selectedSection}
            onChange={(e) => setSelectedSection(e.target.value)}
          >
            <option value="">Select Section</option>
            {sections.map((sec, index) => (
              <option key={index} value={sec}>{sec}</option>
            ))}
          </Form.Control>
        </Form.Group>
        <Form.Group className="form-group">
          <Form.Label>Subject</Form.Label>
          <Form.Control
            as="select"
            value={selectedSubject}
            onChange={(e) => setSelectedSubject(e.target.value)}
          >
            <option value="">Select Subject</option>
            <option value={user.subject}>{user.subject}</option>
          </Form.Control>
        </Form.Group>
        <Button className="btn-primary" onClick={handleFilter} style={{ marginBottom: '1rem' }}>
          Filter Students
        </Button>
      </div>
      {notification && <div className="notification">{notification}</div>}
      {students.length > 0 && (
        <>
          <h3>Mark Attendance</h3>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Student ID</th>
                <th>Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student._id}>
                  <td>{student.studentId}</td>
                  <td>{student.name}</td>
                  <td>
                    <Form.Check
                      type="radio"
                      label="Present"
                      name={`attendance-${student._id}`}
                      checked={attendance[student._id] === 'present'}
                      onChange={() => handleAttendanceChange(student._id, 'present')}
                    />
                    <Form.Check
                      type="radio"
                      label="Absent"
                      name={`attendance-${student._id}`}
                      checked={attendance[student._id] === 'absent'}
                      onChange={() => handleAttendanceChange(student._id, 'absent')}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <div style={{ marginTop: '1rem' }}>
            <p>Summary: Present - {summary.present}, Absent - {summary.absent}</p>
            <Button className="btn-primary" onClick={submitAttendance} style={{ marginRight: '1rem' }}>
              Submit Attendance
            </Button>
            <Button className="btn-primary" onClick={exportAttendance}>
              Export to CSV
            </Button>
          </div>
        </>
      )}
      <h3>Attendance History</h3>
      <Table striped bordered hover>
        <thead>
          <tr>
            <th>Student ID</th>
            <th>Date</th>
            <th>Time</th>
            <th>Subject</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {attendanceHistory.map((record, index) => (
            <tr key={index}>
              <td>{record.studentId}</td>
              <td>{new Date(record.date).toLocaleDateString()}</td>
              <td>{new Date(record.date).toLocaleTimeString()}</td>
              <td>{record.subject || 'N/A'}</td>
              <td style={{ color: record.status === 'present' ? 'green' : 'red' }}>
                {record.status}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showSubmitPopup} onHide={() => setShowSubmitPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{notification.includes('successfully') ? 'Success' : 'Error'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{notification || 'Action completed!'}</Modal.Body>
        <Modal.Footer>
          <Button variant={notification.includes('successfully') ? 'primary' : 'secondary'} onClick={() => setShowSubmitPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default FacultyDashboard;
