import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Modal } from 'react-bootstrap';

function StudentDashboard({ user }) {
  const [attendance, setAttendance] = useState([]);
  const [profile, setProfile] = useState(user || {});
  const [editMode, setEditMode] = useState(false);
  const [schedule, setSchedule] = useState([]);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [showErrorPopup, setShowErrorPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const [notes, setNotes] = useState('');

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!profile.studentId) return;
        const [attendanceRes, scheduleRes] = await Promise.all([
          axios.get(`http://localhost:5000/api/attendance/${profile.studentId}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          }),
          axios.get('http://localhost:5000/api/schedule', {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
          })
        ]);
        setAttendance(attendanceRes.data);
        setSchedule(scheduleRes.data);
      } catch (err) {
        setPopupMessage('Failed to fetch data: ' + (err.response?.data?.msg || err.message));
        setShowErrorPopup(true);
      }
    };
    fetchData();
  }, [profile.studentId]);

  const handleUpdateProfile = async (e) => {
    e.preventDefault();
    try {
      if (!profile._id) throw new Error('User ID is undefined');
      const res = await axios.put(`http://localhost:5000/api/users/${profile._id}`, {
        name: profile.name,
        email: profile.email,
        studentId: profile.studentId,
        branch: profile.branch,
        section: profile.section
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setProfile(res.data);
      setEditMode(false);
      setPopupMessage('Profile updated successfully!');
      setShowSuccessPopup(true);
    } catch (err) {
      console.error('Update profile error:', err);
      setPopupMessage('Failed to update profile: ' + (err.response?.data?.msg || err.message));
      setShowErrorPopup(true);
    }
  };

  const calculateAttendancePercentage = () => {
    if (attendance.length === 0) return 0;
    const presentCount = attendance.filter(a => a.status === 'present').length;
    return ((presentCount / attendance.length) * 100).toFixed(2);
  };

  return (
    <div className="dashboard-content">
      <h2>Welcome, {profile.name || 'Student'}</h2>
      {editMode ? (
        <Form onSubmit={handleUpdateProfile} className="profile-form">
          <Form.Group className="form-group">
            <Form.Label>Name</Form.Label>
            <Form.Control
              type="text"
              value={profile.name || ''}
              onChange={(e) => setProfile({ ...profile, name: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Email</Form.Label>
            <Form.Control
              type="email"
              value={profile.email || ''}
              onChange={(e) => setProfile({ ...profile, email: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Student ID</Form.Label>
            <Form.Control
              type="text"
              value={profile.studentId || ''}
              onChange={(e) => setProfile({ ...profile, studentId: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Branch</Form.Label>
            <Form.Control
              type="text"
              value={profile.branch || ''}
              onChange={(e) => setProfile({ ...profile, branch: e.target.value })}
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Section</Form.Label>
            <Form.Control
              type="text"
              value={profile.section || ''}
              onChange={(e) => setProfile({ ...profile, section: e.target.value })}
              required
            />
          </Form.Group>
          <Button className="btn-primary" type="submit">Save</Button>
          <Button variant="secondary" onClick={() => setEditMode(false)} style={{ marginLeft: '1rem' }}>Cancel</Button>
        </Form>
      ) : (
        <>
          <p>Email: {profile.email || 'N/A'}</p>
          <p>Student ID: {profile.studentId || 'N/A'}</p>
          <p>Branch: {profile.branch || 'N/A'}</p>
          <p>Section: {profile.section || 'N/A'}</p>
          <p>Average Attendance: {calculateAttendancePercentage()}%</p>
          <Button className="btn-primary" onClick={() => setEditMode(true)}>Edit Profile</Button>
          <Form.Group className="form-group" style={{ marginTop: '1rem' }}>
            <Form.Label>Notes</Form.Label>
            <Form.Control
              as="textarea"
              value={notes}
              onChange={(e) => setNotes(e.target.value)}
              placeholder="Add personal notes here..."
            />
          </Form.Group>
        </>
      )}
      <h3>Attendance Records</h3>
      <Table className="table">
        <thead>
          <tr>
            <th>Date</th>
            <th>Time</th>
            <th>Subject</th>
            <th>Status</th>
          </tr>
        </thead>
        <tbody>
          {attendance.map((record) => (
            <tr key={record._id}>
              <td>{new Date(record.date).toLocaleDateString()}</td>
              <td>{new Date(record.date).toLocaleTimeString()}</td>
              <td>{record.subject || 'N/A'}</td>
              <td style={{ color: record.status === 'present' ? 'green' : 'red' }}>
                {record.status}
              </td>
            </tr>
          ))}
        </tbody>
      </Table>
      <h3>Class Schedule</h3>
      <Table className="table">
        <thead>
          <tr>
            <th>Day</th>
            <th>Time</th>
            <th>Subject</th>
          </tr>
        </thead>
        <tbody>
          {schedule.map((classItem, index) => (
            <tr key={index}>
              <td>{classItem.day}</td>
              <td>{classItem.time}</td>
              <td>{classItem.subject}</td>
            </tr>
          ))}
        </tbody>
      </Table>

      <Modal show={showSuccessPopup} onHide={() => setShowSuccessPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Success</Modal.Title>
        </Modal.Header>
        <Modal.Body>{popupMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => setShowSuccessPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showErrorPopup} onHide={() => setShowErrorPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Error</Modal.Title>
        </Modal.Header>
        <Modal.Body>{popupMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => setShowErrorPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default StudentDashboard;
