import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaUser, FaCog, FaSignOutAlt, FaBars } from 'react-icons/fa';

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [profileOpen, setProfileOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo-container">
        <img src="/culogo.png" alt="CU Logo" className="logo-image" />
        <img src="/cu.png" alt="CU" className="logo-image" />
      </div>
      <div className="nav-links">
        {user ? (
          <>
            <div className="dropdown" onMouseEnter={() => setProfileOpen(true)} onMouseLeave={() => setProfileOpen(false)}>
              <button className="dropdown-toggle" onClick={() => setProfileOpen(!profileOpen)}>
                <FaUser /> {user.name}
              </button>
              {profileOpen && (
                <div className="profile-dropdown">
                  <div className="profile-details">
                    <p><strong>Name:</strong> {user.name}</p>
                    {user.role === 'faculty' && <p><strong>Subject:</strong> {user.subject}</p>}
                    {user.role === 'student' && <p><strong>Student ID:</strong> {user.studentId}</p>}
                    <p><strong>Email:</strong> {user.email}</p>
                    <p><strong>Role:</strong> {user.role}</p>
                  </div>
                  <Link to="#" className="dropdown-item" onClick={() => setProfileOpen(false)}>
                    <FaCog /> Settings
                  </Link>
                  <button onClick={handleLogout} className="dropdown-item logout-btn">
                    <FaSignOutAlt /> Sign Out
                  </button>
                </div>
              )}
            </div>
          </>
        ) : (
          <Link to="/" className="login-link">Login</Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
