import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Alert, Modal } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [showPassword, setShowPassword] = useState(false); // State to toggle password input
  const navigate = useNavigate();

  const handleNext = () => {
    if (userIdOrEmail.trim()) {
      setShowPassword(true); // Show password field after entering User ID
    } else {
      setError('Please enter a User ID.');
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        userIdOrEmail,
        password,
      });
      localStorage.setItem('token', response.data.token);
      setUser(response.data.user);
      navigate(response.data.user.role === 'student' ? '/student' : '/faculty');
    } catch (err) {
      setError(err.response?.data?.msg || 'Login failed. Please try again.');
      setTimeout(() => setError(''), 3000);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <img src="/cu.png" alt="CUIMS Logo" className="login-logo" />
        <h2>Login</h2>
        <p>Welcome to University Information Management System - Mohali Campus</p>
        {error && <Alert variant="danger">{error}</Alert>}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Label>Enter User Id</Form.Label>
            <div className="input-with-icon">
              <Form.Control
                type="text"
                value={userIdOrEmail}
                onChange={(e) => setUserIdOrEmail(e.target.value)}
                placeholder="Enter User ID"
                required
              />
              <span className="icon"><i className="bi bi-person"></i></span>
            </div>
          </Form.Group>
          {showPassword && (
            <Form.Group className="form-group">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter Password"
                required
              />
            </Form.Group>
          )}
          <Button
            type={showPassword ? 'submit' : 'button'}
            className="next-button"
            onClick={showPassword ? null : handleNext}
          >
            {showPassword ? 'Login' : 'NEXT'}
          </Button>
          <div className="or-text">OR</div>
          <Button className="register-button" onClick={() => navigate('/register')}>
            CLICK HERE FOR REGISTRATION
          </Button>
          <a href="/forgot-password" className="forgot-password">
            Forgot Password?
          </a>
        </Form>
      </div>
    </div>
  );
}

export default Login;
