import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaUser, FaCog, FaSignOutAlt, FaBars } from 'react-icons/fa';

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo-container">
        <img src="/culogo.png" alt="CU Logo" className="logo-image" />
        <span className="university-name">CHANDIGARH UNIVERSITY</span>
      </div>
      <div className="nav-links">
        {user ? (
          <div className="dropdown">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              className="dropdown-toggle"
            >
              <FaBars />
            </button>
            {dropdownOpen && (
              <div className="dropdown-content">
                <Link to={user.role === 'student' ? '/student' : '/faculty'} onClick={() => setDropdownOpen(false)} className="dropdown-item">
                  <FaUser /> Dashboard
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} className="dropdown-item">
                  <FaUser /> Profile
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} className="dropdown-item">
                  <FaCog /> Settings
                </Link>
                <button onClick={handleLogout} className="dropdown-item logout-btn">
                  <FaSignOutAlt /> Sign Out
                </button>
              </div>
            )}
          </div>
        ) : (
          <Link to="/" className="login-link"></Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
