import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaUser, FaCog, FaSignOutAlt, FaBars } from 'react-icons/fa';

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <img src="https://upload.wikimedia.org/wikipedia/en/thumb/a/a3/Chandigarh_University_logo.svg/1200px-Chandigarh_University_logo.svg.png" alt="CU Logo" />
        CHANDIGARH UNIVERSITY
      </div>
      <div className="nav-links">
        {user ? (
          <div className="dropdown">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                cursor: 'pointer',
                fontSize: '1.5rem',
                padding: '0.5rem',
                display: 'flex',
                alignItems: 'center'
              }}
            >
              <FaBars />
            </button>
            {dropdownOpen && (
              <div className="dropdown-content">
                <Link to={user.role === 'student' ? '/student' : '/faculty'} onClick={() => setDropdownOpen(false)} style={{ display: 'block', padding: '0.5rem 1rem', color: '#333', textDecoration: 'none' }}>
                  <FaUser style={{ marginRight: '0.5rem' }} /> Dashboard
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} style={{ display: 'block', padding: '0.5rem 1rem', color: '#333', textDecoration: 'none' }}>
                  <FaUser style={{ marginRight: '0.5rem' }} /> Profile
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} style={{ display: 'block', padding: '0.5rem 1rem', color: '#333', textDecoration: 'none' }}>
                  <FaCog style={{ marginRight: '0.5rem' }} /> Settings
                </Link>
                <button onClick={() => { handleLogout(); setDropdownOpen(false); }} style={{ width: '100%', textAlign: 'left', padding: '0.5rem 1rem', background: 'none', border: 'none', color: '#333', display: 'block' }}>
                  <FaSignOutAlt style={{ marginRight: '0.5rem' }} /> Sign Out
                </button>
              </div>
            )}
          </div>
        ) : null}
      </div>
    </nav>
  );
}

export default Navbar;
