import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { FaUser, FaGear, FaSignOut, FaBars } from 'react-icons/fa6-solid'; // Corrected imports

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <img src="https://upload.wikimedia.org/wikipedia/en/thumb/a/a3/Chandigarh_University_logo.svg/1200px-Chandigarh_University_logo.svg.png" alt="CU Logo" style={{ width: '40px', marginRight: '10px' }} />
        CHANDIGARH UNIVERSITY
      </div>
      <div className="nav-links">
        {user ? (
          <div className="dropdown">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              style={{
                background: 'none',
                border: 'none',
                color: 'white',
                cursor: 'pointer',
                fontSize: '1.5rem',
                padding: '0.5rem',
                display: 'flex',
                alignItems: 'center'
              }}
            >
              <FaBars />
            </button>
            {dropdownOpen && (
              <div className="dropdown-content" style={{
                minWidth: '200px',
                background: '#fff',
                borderRadius: '5px',
                boxShadow: '0 8px 16px rgba(0,0,0,0.2)',
                position: 'absolute',
                right: 0,
                top: '100%',
                zIndex: 1000
              }}>
                <Link to={user.role === 'student' ? '/student' : '/faculty'} onClick={() => setDropdownOpen(false)} style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem 1rem',
                  color: '#333',
                  textDecoration: 'none',
                  transition: 'background-color 0.3s'
                }}>
                  <FaUser style={{ marginRight: '0.75rem' }} /> Dashboard
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem 1rem',
                  color: '#333',
                  textDecoration: 'none',
                  transition: 'background-color 0.3s'
                }}>
                  <FaUser style={{ marginRight: '0.75rem' }} /> Profile
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} style={{
                  display: 'flex',
                  alignItems: 'center',
                  padding: '0.75rem 1rem',
                  color: '#333',
                  textDecoration: 'none',
                  transition: 'background-color 0.3s'
                }}>
                  <FaGear style={{ marginRight: '0.75rem' }} /> Settings
                </Link>
                <button onClick={() => { handleLogout(); setDropdownOpen(false); }} style={{
                  width: '100%',
                  textAlign: 'left',
                  padding: '0.75rem 1rem',
                  background: 'none',
                  border: 'none',
                  color: '#333',
                  display: 'flex',
                  alignItems: 'center',
                  transition: 'background-color 0.3s'
                }}>
                  <FaSignOut style={{ marginRight: '0.75rem' }} /> Sign Out
                </button>
              </div>
            )}
          </div>
        ) : null}
      </div>
    </nav>
  );
}

export default Navbar;
