import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/login', { userIdOrEmail, password }, {
        headers: { 'Content-Type': 'application/json' }
      });
      localStorage.setItem('token', res.data.token);
      setUser(res.data.user);
      if (res.data.user.role === 'student') {
        navigate('/student');
      } else if (res.data.user.role === 'faculty') {
        navigate('/faculty');
      } else {
        throw new Error('Unknown role');
      }
    } catch (err) {
      const errorMessage = err.response?.data?.msg || err.message || 'Login failed. Please check your credentials.';
      alert(errorMessage);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2 style={{
          color: 'white',
          background: 'linear-gradient(90deg, #d32f2f, #b71c1c)',
          WebkitBackgroundClip: 'text',
          WebkitTextFillColor: 'transparent',
          fontFamily: 'Roboto, sans-serif',
          fontWeight: 'bold',
          fontSize: '2.5rem',
          textAlign: 'center',
          marginBottom: '1rem'
        }}>CUAttendance</h2>
        <p style={{
          color: 'white',
          fontSize: '1.1rem',
          fontWeight: '500',
          textAlign: 'center',
          padding: '0 1rem',
          marginBottom: '2rem'
        }}>
          Welcome to University Attendance Management â€“ Your gateway to seamless attendance tracking!
        </p>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group" controlId="formUserIdOrEmail">
            <Form.Label style={{ color: 'white' }}>User ID or Email</Form.Label>
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="24MC10250 or email@example.com"
              required
              autoComplete="username"
            />
          </Form.Group>
          <Form.Group className="form-group" controlId="formPassword">
            <Form.Label style={{ color: 'white' }}>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
              autoComplete="current-password"
            />
          </Form.Group>
          <Button className="btn-login" type="submit">
            LOGIN
          </Button>
          <a href="/forgot-password" className="forgot-password" style={{ color: 'white' }}>
            Forgot Password?
          </a>
          <Button
            variant="link"
            onClick={() => navigate('/register')}
            style={{ color: 'white', textDecoration: 'none', marginTop: '0.5rem', display: 'block' }}
          >
            Register
          </Button>
        </Form>
      </div>
    </div>
  );
}

export default Login;
