import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Card, Table, Button, Modal, Form } from 'react-bootstrap';
import { Line } from 'react-chartjs-2';
import { toast } from 'react-toastify';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend);

function StudentDashboard({ user, setUser }) {
  const [attendance, setAttendance] = useState([]);
  const [schedule, setSchedule] = useState([]);
  const [profile, setProfile] = useState({ name: user.name, email: user.email });
  const [showProfileModal, setShowProfileModal] = useState(false);
  const [notes, setNotes] = useState(localStorage.getItem(`notes_${user._id}`) || '');
  const [searchQuery, setSearchQuery] = useState('');

  useEffect(() => {
    const fetchAttendance = async () => {
      try {
        const res = await axios.get(`http://localhost:5000/api/attendance?studentId=${user.studentId}`);
        setAttendance(res.data);
      } catch (err) {
        toast.error('Failed to fetch attendance: ' + (err.response?.data?.msg || err.message));
      }
    };
    const fetchSchedule = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/schedule');
        setSchedule(res.data);
      } catch (err) {
        toast.error('Failed to fetch schedule: ' + (err.response?.data?.msg || err.message));
      }
    };
    fetchAttendance();
    fetchSchedule();
  }, [user.studentId]);

  const handleProfileUpdate = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.put(`http://localhost:5000/api/users/${user._id}`, profile);
      const updatedUser = { ...user, name: profile.name, email: profile.email };
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setShowProfileModal(false);
      toast.success('Profile updated successfully!');
    } catch (err) {
      toast.error('Failed to update profile: ' + (err.response?.data?.msg || err.message));
    }
  };

  const handleNotesChange = (e) => {
    setNotes(e.target.value);
    localStorage.setItem(`notes_${user._id}`, e.target.value);
  };

  const calculateAttendancePercentage = () => {
    if (attendance.length === 0) return 0;
    const presentCount = attendance.filter((a) => a.status === 'present').length;
    return ((presentCount / attendance.length) * 100).toFixed(2);
  };

  const filteredSchedule = schedule.filter(
    (s) =>
      s.day.toLowerCase().includes(searchQuery.toLowerCase()) ||
      s.subject.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const attendanceData = {
    labels: attendance.map((a) => new Date(a.date).toLocaleDateString()),
    datasets: [
      {
        label: 'Attendance Status',
        data: attendance.map((a) => (a.status === 'present' ? 1 : 0)),
        borderColor: '#d32f2f',
        backgroundColor: 'rgba(211, 47, 47, 0.2)',
        fill: true,
      },
    ],
  };

  return (
    <div className="dashboard-content">
      <h2>Welcome, {user.name}</h2>
      <p><strong>Student ID:</strong> {user.studentId}</p>
      <p><strong>Section:</strong> {user.section}</p>
      <Button
        variant="secondary"
        className="mb-3"
        onClick={() => setShowProfileModal(true)}
      >
        Edit Profile
      </Button>
      <div className="row">
        <div className="col-md-4">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Profile</Card.Title>
              <p><strong>Email:</strong> {profile.email}</p>
              <p><strong>Student ID:</strong> {user.studentId}</p>
              <p><strong>Branch:</strong> {user.branch}</p>
              <p><strong>Section:</strong> {user.section}</p>
              <p><strong>Attendance:</strong> {calculateAttendancePercentage()}%</p>
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Notes</Card.Title>
              <Form.Control
                as="textarea"
                rows={5}
                value={notes}
                onChange={handleNotesChange}
                placeholder="Add your notes here..."
              />
            </Card.Body>
          </Card>
        </div>
        <div className="col-md-8">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Attendance Records</Card.Title>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Date</th>
                    <th>Subject</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {attendance.map((record) => (
                    <tr key={record._id}>
                      <td>{new Date(record.date).toLocaleDateString()}</td>
                      <td>{record.subject}</td>
                      <td style={{ color: record.status === 'present' ? 'green' : 'red' }}>
                        {record.status}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Attendance Trend</Card.Title>
              <Line data={attendanceData} />
            </Card.Body>
          </Card>
          <Card>
            <Card.Body>
              <Card.Title>Class Schedule</Card.Title>
              <Form.Group className="mb-3">
                <Form.Label>Search Schedule</Form.Label>
                <Form.Control
                  type="text"
                  placeholder="Search by day or subject"
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                />
              </Form.Group>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Day</th>
                    <th>Time</th>
                    <th>Subject</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredSchedule.map((classItem) => (
                    <tr key={classItem._id}>
                      <td>{classItem.day}</td>
                      <td>{classItem.time}</td>
                      <td>{classItem.subject}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </Card.Body>
          </Card>
        </div>
      </div>
      <Modal show={showProfileModal} onHide={() => setShowProfileModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Profile</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleProfileUpdate}>
            <Form.Group className="mb-3">
              <Form.Label>Name</Form.Label>
              <Form.Control
                type="text"
                value={profile.name}
                onChange={(e) => setProfile({ ...profile, name: e.target.value })}
                required
              />
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Email</Form.Label>
              <Form.Control
                type="email"
                value={profile.email}
                onChange={(e) => setProfile({ ...profile, email: e.target.value })}
                required
              />
            </Form.Group>
            <Button className="btn-danger" type="submit">
              Save Changes
            </Button>
          </Form>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default StudentDashboard;
