import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Modal } from 'react-bootstrap';

function ForgotPassword() {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showResetPopup, setShowResetPopup] = useState(false);
  const [resetMessage, setResetMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setResetMessage('Passwords do not match');
      setShowResetPopup(true);
      return;
    }
    if (newPassword.length < 6) {
      setResetMessage('Password must be at least 6 characters long');
      setShowResetPopup(true);
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', {
        userIdOrEmail,
        newPassword
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      setResetMessage(response.data.msg);
      setShowResetPopup(true);
      setTimeout(() => {
        setShowResetPopup(false);
        navigate('/');
      }, 2000); // Redirect after 2 seconds
    } catch (err) {
      console.error('Forgot password error:', err);
      setResetMessage(err.response?.data?.msg || 'Password reset failed. Please try again.');
      setShowResetPopup(true);
    }
  };

  return (
    <div className="dashboard-content">
      <h2>Forgot Password</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="form-group" controlId="formUserIdOrEmail">
          <Form.Label>User ID or Email</Form.Label>
          <Form.Control
            type="text"
            value={userIdOrEmail}
            onChange={(e) => setUserIdOrEmail(e.target.value)}
            placeholder="Enter User ID or Email"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formNewPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="Enter new password"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formConfirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Re-enter new password"
            required
          />
        </Form.Group>
        <Button className="btn-danger" type="submit">Reset Password</Button>
      </Form>
      <Modal show={showResetPopup} onHide={() => setShowResetPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{resetMessage.includes('successfully') ? 'Success' : 'Error'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{resetMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant={resetMessage.includes('successfully') ? 'primary' : 'secondary'} onClick={() => setShowResetPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ForgotPassword;
