import React from 'react';
import { Link } from 'react-router-dom';

function Navbar({ user, setUser }) {
  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo">
        <img src="https://upload.wikimedia.org/wikipedia/en/thumb/a/a3/Chandigarh_University_logo.svg/1200px-Chandigarh_University_logo.svg.png" alt="CU Logo" style={{ width: '40px', marginRight: '10px' }} />
        CHANDIGARH UNIVERSITY
      </div>
      <div className="nav-links">
        <Link to="/">Home</Link>
        {user && <Link to={user.role === 'student' ? '/student' : '/faculty'}>Dashboard</Link>}
        {user && <button onClick={handleLogout} style={{ background: 'none', border: 'none', color: 'white', cursor: 'pointer' }}>Logout</button>}
      </div>
    </nav>
  );
}

export default Navbar;
