import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:5000/api/login', {
        userIdOrEmail,
        password
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      localStorage.setItem('token', response.data.token);
      setUser(response.data.user);
      navigate(response.data.user.role === 'student' ? '/student' : '/faculty');
    } catch (err) {
      setError(err.response?.data?.msg || 'Login failed. Please try again.');
      setTimeout(() => setError(''), 3000);
    }
  };

  const handleRegistration = () => {
    navigate('/register'); // Assuming a register route exists
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Login</h2>
        <p>Welcome back! Please login to your account.</p>
        {error && <div className="error-message">{error}</div>}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Label>User ID or Email</Form.Label>
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="Enter User ID or Email"
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
            />
          </Form.Group>
          <Button type="submit" className="next-button">
            Login
          </Button>
          <div className="or-text">OR</div>
          <Button className="register-button" onClick={handleRegistration}>
            Click HERE FOR REGISTRATION
          </Button>
          <a href="/forgot-password" className="forgot-password"> {/* Changed to lowercase */}
            Forgot Password?
          </a>
        </Form>
      </div>
    </div>
  );
}

export default Login;
