import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ForgotPassword() {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      alert('Passwords do not match');
      return;
    }
    try {
      const response = await axios.post('/api/forgot-password', {
        userIdOrEmail,
        newPassword
      });
      alert(response.data.msg);
      navigate('/');
    } catch (err) {
      alert(err.response?.data?.msg || 'Password reset failed');
    }
  };

  return (
    <div className="dashboard-content">
      <h2>Forgot Password</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="form-group" controlId="formUserIdOrEmail">
          <Form.Label>User ID or Email</Form.Label>
          <Form.Control
            type="text"
            value={userIdOrEmail}
            onChange={(e) => setUserIdOrEmail(e.target.value)}
            placeholder="Enter User ID or Email"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formNewPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="Enter new password"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formConfirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Re-enter new password"
            required
          />
        </Form.Group>
        <Button className="btn-primary" type="submit">
          Reset Password
        </Button>
      </Form>
    </div>
  );
}

export default ForgotPassword;
