import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { FaUser, FaCog, FaSignOutAlt, FaBars } from 'react-icons/fa';
import axios from 'axios';

function Navbar({ user, setUser }) {
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [profileDetails, setProfileDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProfile = async () => {
      if (user) {
        try {
          const token = localStorage.getItem('token');
          const response = await axios.get(`http://localhost:5000/api/users/${user._id}`, {
            headers: { Authorization: `Bearer ${token}` }
          });
          setProfileDetails(response.data);
        } catch (err) {
          console.error('Failed to fetch profile:', err);
        } finally {
          setLoading(false);
        }
      }
    };
    fetchProfile();
  }, [user]);

  const handleLogout = () => {
    localStorage.removeItem('token');
    setUser(null);
  };

  return (
    <nav className="navbar">
      <div className="logo-container">
        <img src="/culogo.png" alt="CU Logo" className="logo-image" />
        <img src="/cu.png" alt="CU" className="logo-image" />
      </div>
      <div className="nav-links">
        {user ? (
          <div className="dropdown">
            <button
              onClick={() => setDropdownOpen(!dropdownOpen)}
              className="dropdown-toggle"
            >
              <FaBars />
            </button>
            {dropdownOpen && (
              <div className="dropdown-content">
                <div className="profile-section">
                  {loading ? (
                    <p>Loading...</p>
                  ) : profileDetails ? (
                    <>
                      <p><strong>Name:</strong> {profileDetails.name}</p>
                      <p><strong>Email:</strong> {profileDetails.email}</p>
                      {user.role === 'faculty' && <p><strong>Subject:</strong> {profileDetails.subject}</p>}
                      {user.role === 'student' && (
                        <>
                          <p><strong>Student ID:</strong> {profileDetails.studentId}</p>
                          <p><strong>Section:</strong> {profileDetails.section}</p>
                        </>
                      )}
                    </>
                  ) : (
                    <p>No profile data available</p>
                  )}
                </div>
                <Link to={user.role === 'student' ? '/student' : '/faculty'} onClick={() => setDropdownOpen(false)} className="dropdown-item">
                  <FaUser /> Dashboard
                </Link>
                <Link to="#" onClick={() => setDropdownOpen(false)} className="dropdown-item">
                  <FaCog /> Settings
                </Link>
                <button onClick={handleLogout} className="dropdown-item logout-btn">
                  <FaSignOutAlt /> Sign Out
                </button>
              </div>
            )}
          </div>
        ) : (
          <Link to="/" className="login-link">Login</Link>
        )}
      </div>
    </nav>
  );
}

export default Navbar;
