import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Modal } from 'react-bootstrap';

function ForgotPassword() {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [showErrorPopup, setShowErrorPopup] = useState(false);
  const [popupMessage, setPopupMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setPopupMessage('Passwords do not match');
      setShowErrorPopup(true);
      return;
    }
    try {
      const response = await axios.post('/api/forgot-password', {
        userIdOrEmail,
        newPassword
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      setPopupMessage(response.data.msg);
      setShowSuccessPopup(true);
      setTimeout(() => navigate('/'), 2000); // Redirect after 2 seconds
    } catch (err) {
      console.error('Forgot password error:', err);
      setPopupMessage(err.response?.data?.msg || 'Password reset failed. Please try again.');
      setShowErrorPopup(true);
    }
  };

  return (
    <div className="dashboard-content">
      <h2>Forgot Password</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group className="form-group" controlId="formUserIdOrEmail">
          <Form.Label>User ID or Email</Form.Label>
          <Form.Control
            type="text"
            value={userIdOrEmail}
            onChange={(e) => setUserIdOrEmail(e.target.value)}
            placeholder="Enter User ID or Email"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formNewPassword">
          <Form.Label>New Password</Form.Label>
          <Form.Control
            type="password"
            value={newPassword}
            onChange={(e) => setNewPassword(e.target.value)}
            placeholder="Enter new password"
            required
          />
        </Form.Group>
        <Form.Group className="form-group" controlId="formConfirmPassword">
          <Form.Label>Confirm Password</Form.Label>
          <Form.Control
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            placeholder="Re-enter new password"
            required
          />
        </Form.Group>
        <Button className="btn-primary" type="submit">
          Reset Password
        </Button>
      </Form>

      <Modal show={showSuccessPopup} onHide={() => setShowSuccessPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Success</Modal.Title>
        </Modal.Header>
        <Modal.Body>{popupMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => setShowSuccessPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>

      <Modal show={showErrorPopup} onHide={() => setShowErrorPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Error</Modal.Title>
        </Modal.Header>
        <Modal.Body>{popupMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant="primary" onClick={() => setShowErrorPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ForgotPassword;
