import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import axios from 'axios';
import Login from './components/Login';
import StudentDashboard from './components/StudentDashboard';
import ForgotPassword from './components/ForgotPassword';
import FacultyDashboard from './components/FacultyDashboard';
import Navbar from './components/Navbar';
import './styles.css';

function App() {
  const [user, setUser] = useState(null);

  useEffect(() => {
    const token = localStorage.getItem('token');
    if (token) {
      axios.defaults.headers.common['Authorization'] = `Bearer ${token}`;
      axios.get('http://localhost:5000/api/validate', {
        headers: { Authorization: `Bearer ${token}` }
      }).then(res => {
        setUser(res.data.user);
      }).catch(() => {
        localStorage.removeItem('token');
        setUser(null);
      });
    }
  }, []);

  return (
    <Router>
      <div className="app">
        <Navbar user={user} setUser={setUser} />
        <div className="content">
          <Routes>
            <Route path="/" element={user ? <Navigate to={user.role === 'student' ? '/student' : '/faculty'} /> : <Login setUser={setUser} />} />
            <Route path="/student" element={user && user.role === 'student' ? <StudentDashboard user={user} /> : <Navigate to="/" />} />
            <Route path="/faculty" element={user && user.role === 'faculty' ? <FacultyDashboard user={user} /> : <Navigate to="/" />} />
            <Route path="/forgot-password" element={<ForgotPassword />} />
            <Route path="/register" element={<div>Register Page (To be implemented)</div>} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
