import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table } from 'react-bootstrap';

function FacultyDashboard({ user }) {
  const [classes, setClasses] = useState([]);
  const [selectedClass, setSelectedClass] = useState('');
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});

  useEffect(() => {
    const fetchClasses = async () => {
      try {
        const res = await axios.get('/api/classes', { // Relative path
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        });
        setClasses(res.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchClasses();
  }, []);

  const handleClassSelect = async (e) => {
    const className = e.target.value;
    setSelectedClass(className);
    try {
      const res = await axios.get(`/api/users?class=${className}`, { // Relative path
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setStudents(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const handleAttendanceChange = (studentId, status) => {
    setAttendance({ ...attendance, [studentId]: status });
  };

  const submitAttendance = async () => {
    try {
      for (const [studentId, status] of Object.entries(attendance)) {
        await axios.post(
          '/api/attendance', // Relative path
          {
            studentId,
            class: selectedClass,
            subject: classes.find((c) => c.name === selectedClass)?.subject || 'General',
            status
          },
          { headers: { Authorization: `Bearer ${localStorage.getItem('token')}` } }
        );
      }
      alert('Attendance submitted');
    } catch (err) {
      alert('Failed to submit attendance');
    }
  };

  return (
    <div className="mt-5">
      <h2>Welcome, {user.name}</h2>
      <Form.Group className="mb-3">
        <Form.Label>Select Class</Form.Label>
        <Form.Select value={selectedClass} onChange={handleClassSelect}>
          <option value="">--Select--</option>
          {classes.map((c) => (
            <option key={c._id} value={c.name}>
              {c.name} - {c.subject}
            </option>
          ))}
        </Form.Select>
      </Form.Group>
      {students.length > 0 && (
        <>
          <h3>Mark Attendance</h3>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>Student Name</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {students.map((student) => (
                <tr key={student._id}>
                  <td>{student.name}</td>
                  <td>
                    <Form.Check
                      type="radio"
                      label="Present"
                      name={`attendance-${student._id}`}
                      onChange={() => handleAttendanceChange(student.studentId, 'present')}
                    />
                    <Form.Check
                      type="radio"
                      label="Absent"
                      name={`attendance-${student._id}`}
                      onChange={() => handleAttendanceChange(student.studentId, 'absent')}
                    />
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
          <Button variant="primary" onClick={submitAttendance}>
            Submit Attendance
          </Button>
        </>
      )}
    </div>
  );
}

export default FacultyDashboard;
