import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Form, Button, Table, Modal, Card, Alert, Tab, Tabs } from 'react-bootstrap';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function FacultyDashboard({ user }) {
  const [sections, setSections] = useState([]);
  const [selectedSection, setSelectedSection] = useState('');
  const [selectedSubject, setSelectedSubject] = useState('');
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().split('T')[0]);
  const [students, setStudents] = useState([]);
  const [attendance, setAttendance] = useState({});
  const [summary, setSummary] = useState({ present: 0, absent: 0 });
  const [message, setMessage] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [schedule, setSchedule] = useState([]);
  const [newSchedule, setNewSchedule] = useState({ day: '', time: '', subject: '', section: '' });
  const [editScheduleId, setEditScheduleId] = useState(null);
  const [attendanceHistory, setAttendanceHistory] = useState([]);
  const [analytics, setAnalytics] = useState({});

  useEffect(() => {
    const fetchSections = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/sections', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setSections(res.data);
      } catch (err) {
        setMessage('Failed to fetch sections: ' + (err.response?.data?.msg || err.message));
        setShowModal(true);
      }
    };
    const fetchSchedule = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/schedule', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setSchedule(res.data);
      } catch (err) {
        setMessage('Failed to fetch schedule: ' + (err.response?.data?.msg || err.message));
        setShowModal(true);
      }
    };
    const fetchAttendanceHistory = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/attendance', {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        });
        setAttendanceHistory(res.data);
      } catch (err) {
        setMessage('Failed to fetch attendance history: ' + (err.response?.data?.msg || err.message));
        setShowModal(true);
      }
    };
    fetchSections();
    fetchSchedule();
    fetchAttendanceHistory();
  }, []);

  const handleFilter = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/users?section=${selectedSection}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setStudents(res.data);
      setSummary({ present: 0, absent: 0 });
      setAttendance({});
      const dateAttendance = await axios.get(
        `http://localhost:5000/api/attendance?date=${selectedDate}&section=${selectedSection}`,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      const initialAttendance = dateAttendance.data.reduce((acc, record) => {
        acc[record.studentId] = record.status;
        return acc;
      }, {});
      setAttendance(initialAttendance);
      updateSummary(initialAttendance);

      // Fetch analytics
      const analyticsRes = await axios.get(
        `http://localhost:5000/api/attendance?section=${selectedSection}&startDate=${
          new Date(selectedDate).setDate(new Date(selectedDate).getDate() - 30)
        }&endDate=${selectedDate}`,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      const analyticsData = analyticsRes.data.reduce((acc, record) => {
        acc[record.studentId] = acc[record.studentId] || { present: 0, absent: 0 };
        acc[record.studentId][record.status]++;
        return acc;
      }, {});
      setAnalytics(analyticsData);
    } catch (err) {
      setMessage('Failed to filter students: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
    }
  };

  const updateSummary = (attendanceData) => {
    const count = { present: 0, absent: 0 };
    Object.values(attendanceData).forEach((status) => {
      if (status === 'present') count.present++;
      if (status === 'absent') count.absent++;
    });
    setSummary(count);
  };

  const handleAttendanceChange = (studentId, status) => {
    setAttendance((prev) => {
      const newAttendance = { ...prev, [studentId]: status };
      updateSummary(newAttendance);
      return newAttendance;
    });
  };

  const submitAttendance = async () => {
    try {
      for (const [studentId, status] of Object.entries(attendance)) {
        const existing = await axios.get(
          `http://localhost:5000/api/attendance?studentId=${studentId}&date=${selectedDate}`,
          {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
          }
        );
        const existingRecord = existing.data[0];
        if (existingRecord) {
          await axios.put(
            `http://localhost:5000/api/attendance/${existingRecord._id}`,
            { status },
            {
              headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
            }
          );
        } else {
          await axios.post(
            'http://localhost:5000/api/attendance',
            {
              studentId,
              section: selectedSection,
              subject: selectedSubject || user.subject,
              status,
              date: selectedDate,
            },
            {
              headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
            }
          );
        }
      }
      setMessage('Attendance submitted successfully!');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    } catch (err) {
      setMessage('Failed to submit attendance: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  };

  const exportAttendance = () => {
    const csv = [
      ['Student ID', 'Name', 'Status', 'Date', 'Time'],
      ...students.map((student) => [
        student.studentId,
        student.name,
        attendance[student.studentId] || 'N/A',
        selectedDate,
        new Date().toLocaleTimeString(),
      ]),
    ]
      .map((row) => row.join(','))
      .join('\n');
    const blob = new Blob([csv], { type: 'text/csv' });
    const url = window.URL.createObjectURL(blob);
    const a = document.createElement('a');
    a.href = url;
    a.download = `attendance_${selectedSection}_${selectedDate}.csv`;
    a.click();
    window.URL.revokeObjectURL(url);
  };

  const handleCreateSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.post('http://localhost:5000/api/schedule', newSchedule, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setNewSchedule({ day: '', time: '', subject: '', section: '' });
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSchedule(res.data);
      setMessage('Schedule created successfully!');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    } catch (err) {
      setMessage('Failed to create schedule: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  };

  const handleEditSchedule = (id) => {
    const scheduleToEdit = schedule.find((s) => s._id === id);
    setNewSchedule({
      day: scheduleToEdit.day,
      time: scheduleToEdit.time,
      subject: scheduleToEdit.subject,
      section: scheduleToEdit.section,
    });
    setEditScheduleId(id);
  };

  const handleUpdateSchedule = async (e) => {
    e.preventDefault();
    try {
      await axios.put(
        `http://localhost:5000/api/schedule/${editScheduleId}`,
        newSchedule,
        {
          headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
        }
      );
      setNewSchedule({ day: '', time: '', subject: '', section: '' });
      setEditScheduleId(null);
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSchedule(res.data);
      setMessage('Schedule updated successfully!');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    } catch (err) {
      setMessage('Failed to update schedule: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  };

  const handleDeleteSchedule = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/schedule/${id}`, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      const res = await axios.get('http://localhost:5000/api/schedule', {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` },
      });
      setSchedule(res.data);
      setMessage('Schedule deleted successfully!');
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    } catch (err) {
      setMessage('Failed to delete schedule: ' + (err.response?.data?.msg || err.message));
      setShowModal(true);
      setTimeout(() => setShowModal(false), 3000);
    }
  };

  const analyticsData = {
    labels: students.map((s) => s.name),
    datasets: [
      {
        label: 'Present',
        data: students.map((s) => analytics[s.studentId]?.present || 0),
        backgroundColor: '#d32f2f',
      },
      {
        label: 'Absent',
        data: students.map((s) => analytics[s.studentId]?.absent || 0),
        backgroundColor: '#b71c1c',
      },
    ],
  };

  return (
    <div className="dashboard-content">
      <h2>Welcome, {user.name}</h2>
      <p><strong>Subject:</strong> {user.subject}</p>
      <Tabs defaultActiveKey="attendance" className="mb-4">
        <Tab eventKey="attendance" title="Mark Attendance">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Attendance Management</Card.Title>
              <Form>
                <div className="row">
                  <div className="col-md-4">
                    <Form.Group className="form-group">
                      <Form.Label>Date</Form.Label>
                      <Form.Control
                        type="date"
                        value={selectedDate}
                        onChange={(e) => setSelectedDate(e.target.value)}
                        required
                      />
                    </Form.Group>
                  </div>
                  <div className="col-md-4">
                    <Form.Group className="form-group">
                      <Form.Label>Section</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedSection}
                        onChange={(e) => setSelectedSection(e.target.value)}
                      >
                        <option value="">Select Section</option>
                        {sections.map((sec) => (
                          <option key={sec} value={sec}>
                            {sec}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>
                  </div>
                  <div className="col-md-4">
                    <Form.Group className="form-group">
                      <Form.Label>Subject</Form.Label>
                      <Form.Control
                        as="select"
                        value={selectedSubject}
                        onChange={(e) => setSelectedSubject(e.target.value)}
                      >
                        <option value="">Select Subject</option>
                        <option value={user.subject}>{user.subject}</option>
                        {schedule
                          .filter((s) => s.section === selectedSection)
                          .map((s) =>
                            s.subject !== user.subject ? (
                              <option key={s._id} value={s.subject}>
                                {s.subject}
                              </option>
                            ) : null
                          )}
                      </Form.Control>
                    </Form.Group>
                  </div>
                </div>
                <Button className="btn-danger" onClick={handleFilter}>
                  Filter Students
                </Button>
              </Form>
              {students.length > 0 && (
                <>
                  <h4 className="mt-4">Mark Attendance for {new Date(selectedDate).toLocaleDateString()}</h4>
                  <Table striped bordered hover>
                    <thead>
                      <tr>
                        <th>Student ID</th>
                        <th>Name</th>
                        <th>Status</th>
                      </tr>
                    </thead>
                    <tbody>
                      {students.map((student) => (
                        <tr key={student._id}>
                          <td>{student.studentId}</td>
                          <td>{student.name}</td>
                          <td>
                            <Form.Check
                              type="radio"
                              label="Present"
                              name={`attendance-${student._id}`}
                              checked={attendance[student.studentId] === 'present'}
                              onChange={() => handleAttendanceChange(student.studentId, 'present')}
                            />
                            <Form.Check
                              type="radio"
                              label="Absent"
                              name={`attendance-${student._id}`}
                              checked={attendance[student.studentId] === 'absent'}
                              onChange={() => handleAttendanceChange(student.studentId, 'absent')}
                            />
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </Table>
                  <p>
                    <strong>Summary:</strong> Present - {summary.present}, Absent - {summary.absent}
                  </p>
                  <Button className="btn-danger me-2" onClick={submitAttendance}>
                    Submit Attendance
                  </Button>
                  <Button className="btn-danger" onClick={exportAttendance}>
                    Export to CSV
                  </Button>
                </>
              )}
            </Card.Body>
          </Card>
        </Tab>
        <Tab eventKey="schedule" title="Manage Schedule">
          <Card className="mb-4">
            <Card.Body>
              <Card.Title>Class Schedule</Card.Title>
              <Table striped bordered hover>
                <thead>
                  <tr>
                    <th>Day</th>
                    <th>Time</th>
                    <th>Subject</th>
                    <th>Section</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {schedule.map((classItem) => (
                    <tr key={classItem._id}>
                      <td>{classItem.day}</td>
                      <td>{classItem.time}</td>
                      <td>{classItem.subject}</td>
                      <td>{classItem.section}</td>
                      <td>
                        <Button
                          variant="warning"
                          onClick={() => handleEditSchedule(classItem._id)}
                          className="me-2"
                        >
                          Edit
                        </Button>
                        <Button variant="danger" onClick={() => handleDeleteSchedule(classItem._id)}>
                          Delete
                        </Button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </Table>
              <Form onSubmit={editScheduleId ? handleUpdateSchedule : handleCreateSchedule}>
                <div className="row">
                  <div className="col-md-3">
                    <Form.Group className="form-group">
                      <Form.Label>Day</Form.Label>
                      <Form.Control
                        as="select"
                        value={newSchedule.day}
                        onChange={(e) =>
                          setNewSchedule({ ...newSchedule, day: e.target.value })
                        }
                        required
                      >
                        <option value="">Select Day</option>
                        {['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday', 'Sunday'].map(
                          (day) => (
                            <option key={day} value={day}>
                              {day}
                            </option>
                          )
                        )}
                      </Form.Control>
                    </Form.Group>
                  </div>
                  <div className="col-md-3">
                    <Form.Group className="form-group">
                      <Form.Label>Time</Form.Label>
                      <Form.Control
                        type="text"
                        value={newSchedule.time}
                        onChange={(e) =>
                          setNewSchedule({ ...newSchedule, time: e.target.value })
                        }
                        placeholder="e.g., 10:00 AM - 11:30 AM"
                        required
                      />
                    </Form.Group>
                  </div>
                  <div className="col-md-3">
                    <Form.Group className="form-group">
                      <Form.Label>Subject</Form.Label>
                      <Form.Control
                        type="text"
                        value={newSchedule.subject}
                        onChange={(e) =>
                          setNewSchedule({ ...newSchedule, subject: e.target.value })
                        }
                        placeholder="Enter subject"
                        required
                      />
                    </Form.Group>
                  </div>
                  <div className="col-md-3">
                    <Form.Group className="form-group">
                      <Form.Label>Section</Form.Label>
                      <Form.Control
                        as="select"
                        value={newSchedule.section}
                        onChange={(e) =>
                          setNewSchedule({ ...newSchedule, section: e.target.value })
                        }
                        required
                      >
                        <option value="">Select Section</option>
                        {sections.map((sec) => (
                          <option key={sec} value={sec}>
                            {sec}
                          </option>
                        ))}
                      </Form.Control>
                    </Form.Group>
                  </div>
                </div>
                <Button className="btn-danger" type="submit">
                  {editScheduleId ? 'Update Schedule' : 'Add Schedule'}
                </Button>
              </Form>
            </Card.Body>
          </Card>
        </Tab>
        <Tab eventKey="analytics" title="Analytics">
          <Card>
            <Card.Body>
              <Card.Title>Attendance Analytics</Card.Title>
              {Object.keys(analytics).length > 0 && (
                <Bar
                  data={analyticsData}
                  options={{
                    responsive: true,
                    plugins: {
                      legend: { position: 'top' },
                      title: { display: true, text: '30-Day Attendance Analytics' },
                    },
                  }}
                />
              )}
            </Card.Body>
          </Card>
        </Tab>
      </Tabs>
      <Modal show={showModal} onHide={() => setShowModal(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{message.includes('successfully') ? 'Success' : 'Error'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{message}</Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowModal(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default FacultyDashboard;
