import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function ForgotPassword() {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (newPassword !== confirmPassword) {
      setError('Passwords do not match');
      setTimeout(() => setError(''), 3000);
      return;
    }
    if (newPassword.length < 6) {
      setError('Password must be at least 6 characters long');
      setTimeout(() => setError(''), 3000);
      return;
    }
    try {
      const response = await axios.post('http://localhost:5000/api/forgot-password', {
        userIdOrEmail,
        newPassword
      }, {
        headers: { 'Content-Type': 'application/json' }
      });
      setSuccess(response.data.msg);
      setTimeout(() => {
        setSuccess('');
        navigate('/login');
      }, 2000);
    } catch (err) {
      setError(err.response?.data?.msg || 'Password reset failed');
      setTimeout(() => setError(''), 3000);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Forgot Password</h2>
        <p>Reset your password here.</p>
        {error && <div className="error-message">{error}</div>}
        {success && <div className="success-message">{success}</div>}
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Label>User ID or Email</Form.Label>
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="Enter User ID or Email"
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>New Password</Form.Label>
            <Form.Control
              type="password"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
              placeholder="Enter New Password"
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Confirm Password</Form.Label>
            <Form.Control
              type="password"
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              placeholder="Confirm New Password"
              required
            />
          </Form.Group>
          <Button className="btn-custom-red" type="submit">
            Reset Password
          </Button>
          <a href="/login" className="forgot-password" style={{ marginTop: '1rem', display: 'block' }}>
            Back to Login
          </a>
        </Form>
      </div>
    </div>
  );
}

export default ForgotPassword;
