import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/login', { userIdOrEmail, password });
      localStorage.setItem('token', res.data.token);
      setUser(res.data.user);
      if (res.data.user.role === 'student') navigate('/student');
      else navigate('/faculty');
    } catch (err) {
      alert(err.response?.data?.msg || 'Login failed');
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Log in</h2>
        <p>Welcome to University Information Management System</p>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="24MC10250 or email@example.com"
              required
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
            />
          </Form.Group>
          <Button className="btn-login" type="submit">
            LOGIN
          </Button>
          <a href="#" className="forgot-password">Forgot Password?</a>
          <Button
            variant="link"
            onClick={() => navigate('/register')}
            style={{ color: '#1976d2', textDecoration: 'none', marginTop: '0.5rem', display: 'block' }}
          >
            Register
          </Button>
        </Form>
      </div>
    </div>
  );
}

export default Login;
