import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Register from './components/Register';
import StudentDashboard from './components/StudentDashboard';
import FacultyDashboard from './components/FacultyDashboard';

function App() {
  const [user, setUser] = useState(null);

  return (
    <Router>
      <div className="container">
        <h1 className="text-center my-4">Chandigarh University Attendance System</h1>
        <Routes>
          <Route path="/" element={<Login setUser={setUser} />} />
          <Route path="/register" element={<Register />} />
          <Route
            path="/student"
            element={user && user.role === 'student' ? <StudentDashboard user={user} /> : <Navigate to="/" />}
          />
          <Route
            path="/faculty"
            element={user && user.role === 'faculty' ? <FacultyDashboard user={user} /> : <Navigate to="/" />}
          />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
