import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Form, Button, Modal } from 'react-bootstrap';

function Login({ setUser }) {
  const [userIdOrEmail, setUserIdOrEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showLoginPopup, setShowLoginPopup] = useState(false);
  const [loginMessage, setLoginMessage] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await axios.post('/api/login', { userIdOrEmail, password }, {
        headers: { 'Content-Type': 'application/json' }
      });
      localStorage.setItem('token', res.data.token);
      setUser(res.data.user);
      setLoginMessage('Login successful!');
      setShowLoginPopup(true);
      setTimeout(() => {
        if (res.data.user.role === 'student') {
          navigate('/student');
        } else if (res.data.user.role === 'faculty') {
          navigate('/faculty');
        }
      }, 1000); // Delay to show popup
    } catch (err) {
      const errorMessage = err.response?.data?.msg || err.message || 'Login failed. Please check your credentials.';
      setLoginMessage(errorMessage);
      setShowLoginPopup(true);
    }
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>CUAttendance</h2>
        <p>Welcome to University Attendance Management â€“ Your gateway to seamless attendance tracking!</p>
        <Form onSubmit={handleSubmit}>
          <Form.Group className="form-group">
            <Form.Label>User ID or Email</Form.Label>
            <Form.Control
              type="text"
              value={userIdOrEmail}
              onChange={(e) => setUserIdOrEmail(e.target.value)}
              placeholder="24MC10250 or email@example.com"
              required
              autoComplete="username"
            />
          </Form.Group>
          <Form.Group className="form-group">
            <Form.Label>Password</Form.Label>
            <Form.Control
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Enter Password"
              required
              autoComplete="current-password"
            />
          </Form.Group>
          <Button className="btn-login" type="submit">LOGIN</Button>
          <a href="/forgot-password" className="forgot-password">Forgot Password?</a>
          <Button
            variant="link"
            onClick={() => navigate('/register')}
            style={{ color: 'white', textDecoration: 'none', marginTop: '0.5rem', display: 'block' }}
          >
            Register
          </Button>
        </Form>
      </div>
      <Modal show={showLoginPopup} onHide={() => setShowLoginPopup(false)}>
        <Modal.Header closeButton>
          <Modal.Title>{loginMessage.includes('successful') ? 'Success' : 'Error'}</Modal.Title>
        </Modal.Header>
        <Modal.Body>{loginMessage}</Modal.Body>
        <Modal.Footer>
          <Button variant={loginMessage.includes('successful') ? 'primary' : 'secondary'} onClick={() => setShowLoginPopup(false)}>
            Close
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default Login;
